    public void createTable(SQLiteDatabase db) {
        db.execSQL("DROP TABLE IF EXISTS gejala");
        db.execSQL("CREATE TABLE if not exists gejala (id TEXT PRIMARY KEY, nama TEXT, " +
                "ya VARCHAR(5), tidak VARCHAR(5), mulai VARCHAR(5), selesai VARCHAR(5));");
    }


    public void createTable3(SQLiteDatabase db3) {
        db3.execSQL("DROP TABLE IF EXISTS learning");
        db3.execSQL("CREATE TABLE if not exists learning (kode_learning TEXT PRIMARY KEY , " +
                "nama_learning TEXT, deskripsi TEXT, gambar TEXT);");
    }

    public void createTable4(SQLiteDatabase db4) {
        db4.execSQL("DROP TABLE IF EXISTS merawat");
        db4.execSQL("CREATE TABLE if not exists merawat (kode_merawat TEXT PRIMARY KEY , " +
                "nama_merawat TEXT, deskripsi_merawat TEXT, referensi_merawat TEXT);");
    }

    public void createTable5(SQLiteDatabase db5) {
        db5.execSQL("DROP TABLE IF EXISTS bayi");
        db5.execSQL("CREATE TABLE if not exists bayi (kode_merawat TEXT PRIMARY KEY , " +
                "nama_merawat TEXT, deskripsi_merawat TEXT, referensi_merawat TEXT);");
    }

    public void createTable6(SQLiteDatabase db6) {
        db6.execSQL("DROP TABLE IF EXISTS melahirkan");
        db6.execSQL("CREATE TABLE if not exists melahirkan (kode_merawat TEXT PRIMARY KEY , " +
                "nama_merawat TEXT, deskripsi_merawat TEXT, referensi_merawat TEXT);");
    }

    public void createTable7(SQLiteDatabase db7) {
        db7.execSQL("DROP TABLE IF EXISTS ibu");
        db7.execSQL("CREATE TABLE if not exists ibu (kode_ibu TEXT PRIMARY KEY , " +
                "nama_penyakit_ibu TEXT, deskripsi_penyakit_ibu TEXT, referensi_penyakit_ibu TEXT);");
    }



